sourcesContent:
import express from 'express';
import userRoutes from './routes/users.js'; // Ajusta la ruta según tu proyecto

const app = express();

app.use(express.json());  // Middleware para manejar JSON

// Usa las rutas que definiste
app.use(userRoutes);

const PORT = 3000;
app.listen(PORT, () => {
  console.log(`Servidor corriendo en el puerto ${PORT}`);
});

export default app;

